import React from "react";
import forecastCSS from "../styles/forecast.module.css";

function ForecastGraph({ data }) {
  // finding the maximum and minimum values in the array
  console.log(data);
  let max = Math.max(...data.temperature),
    min = Math.min(...data.temperature);
  // finding the difference to be able to calculate by how much the height of the line should be curved
  let difference = max - min;
  // the max height to not go out of boundaries
  let maxHeight = data.heightOfContainers;
  let degreeOfChange = maxHeight / difference; // degree of change of Y axis

  // the initial values of the graph
  const temperatureData = {
    x1: 0,
    y1: 0,
    x2: -data.startingPoint,
    y2: 50,
  };

  return (
    <>
      <svg className={forecastCSS.graph}>
        {data.temperature.map((each, key) => {
          temperatureData.x1 = temperature.x2;
          temperature.y1 = temperature.y2;
          temperature.x2 += data.widthOfContainers;

          if (data.temperatureDay[key - 1] > each) {
            temperature.y2 +=
              degreeOfChange * Math.abs(data.temperatureDay[key - 1] - each);
          } else if (data.temperatureDay[key - 1] < each) {
            temperature.y2 -=
              degreeOfChange * Math.abs(data.temperatureDay[key - 1] - each);
          } else {
            temperature.y2 += 0;
          }

          return (
            <g key={key}>
              <circle
                cx={temperature.x2}
                cy={temperature.y2}
                r="4px"
                stroke="white"
                strokeWidth="1"
                fill="black"
              />
              <line
                x1={temperature.x1}
                y1={temperature.y1}
                x2={temperature.x2}
                y2={temperature.y2}
                stroke="white"
              />
            </g>
          );
        })}
      </svg>
    </>
  );
}

export default ForecastGraph;
