import React from "react";
import forecastCSS from "../styles/forecast.module.css";

const temperatureDay = [23, 23, 24, 24, 22];
const temperatureNight = [13, 13, 14, 14, 13];

// 25 - max degree
// min degree - 13
// average degree = 25+13/2 = 22.5
// find the difference between each degree and the average degree
//  

// 100/1.2 - the hegiht
// how much is one degree?


const temperatureData = [
  { x1: "60", y1: "50", x2: "180", y2: "70" },
  { x1: "70", y1: "70", x2: "200", y2: "15" },
];
function ForecastGraph() {
  return (
    <>
      <svg className={forecastCSS.graph}>
        <circle
          cx={temperatureData[0].x1}
          cy={temperatureData[0].y1}
          r="4px"
          stroke="white"
          strokeWidth="1"
          fill="black"
        />
        {temperatureData.map((each, key) => (
          <g>
            <circle
              cx={each.x2}
              cy={each.y2}
              r="4px"
              stroke="white"
              strokeWidth="1"
              fill="black"
            />
            <line
              x1={each.x1}
              y1={each.y1}
              x2={each.x2}
              y2={each.y2}
              stroke="white"
            />
          </g>
        ))}
      </svg>
    </>
  );
}

export default ForecastGraph;
