import React from "react";
import forecastCSS from "../styles/forecast.module.css";

// max degree - 25
// min degree - 13
// average degree = 25+13/2 = 19(no)
// find the difference between either of degrees and the average degree(no)
// the difference between max and min - 12degrees
// 100/1.5~ = 83- the hegiht
// how much is one degree (proportion)
// 12degree == 83px
// 1degree = 83px/12 = ~7px
function ForecastGraph(props) {
  const temperatureDay = [23, 23, 24, 24, 22];
  let max = Math.max(...temperatureDay),
    min = Math.min(...temperatureDay);
  let difference = max - min;
  let maxHeight = 60;
  let degreeOfChange = maxHeight / difference; //of Y axis
  // console.log(props.props.startingPoint);
  console.log("degree of change", degreeOfChange);
  console.log("average", (max + min) / 2);
  const temperatureData = {
    x1: props.props.startingPoint,
    y1: 50,
    x2: props.props.startingPoint ,
    y2: 50 ,
  };

  return (
    <>
      <svg className={forecastCSS.graph}>
        <circle
          cx={temperatureData.x1}
          cy={temperatureData.y1}
          r="4px"
          stroke="white"
          strokeWidth="1"
          fill="black"
        />
        {temperatureDay.map((each, key) => {
          temperatureData.x1= temperatureData.x2;
          temperatureData.y1 = temperatureData.y2;
          temperatureData.x2 += props.props.widthOfContainers;
          if(((max+min))){
          temperatureData.y2 -= degreeOfChange;
          }else if(each <((max+min)/2)){
            temperatureData.y2 += degreeOfChange;
          }else{
            temperatureData.y2 = 50;
          }
          return (
            <g key={key}>
              <circle
                cx={temperatureData.x1}
                cy={temperatureData.y1}
                r="4px"
                stroke="white"
                strokeWidth="1"
                fill="black"
              />
              <line
                x1={temperatureData.x1}
                y1={temperatureData.y1}
                x2={temperatureData.x2}
                y2={temperatureData.y2}
                stroke="white"
              />
            </g>
          );
        })}
      </svg>
    </>
  );
}

export default ForecastGraph;
